import { useEffect, useState } from 'react'
// contract
import contract from './contracts/NFTCollectible.json'
import './App.css'
// contract address
const contractAddress = "0xc5d404183cb9De6a14eccb849f44A862a43c2C05"
// abi
const abt = contract.abi
function App() {
  const [currentAccount, setCurrentAccount] = useState(null);
  const checkWalletIsConnected = async () => {
    // 1.check if there is metamask
    const { etherenum } = window;
    if (!etherenum) {
      alert("Please install Meta mask!")
    }
    console.log("we are ready to go!")
    // 2.chekc if get the account 
    try {

      // 3.yes, then setState the account
      const account = await etherenum.request({ method: 'eth_requestAccounts' })
      console.log(`find the account ${account}`)
      setCurrentAccount(account)
    } catch (error) {
      console.log(error)
    }
  }

  const connectWalletHandler = () => { }

  const mintNftHandler = () => { }

  const connectWalletButton = () => {
    return (
      <button onClick={connectWalletHandler} className='cta-button connect-wallet-button'>
        Connect Wallet
      </button>
    )
  }

  const mintNftButton = () => {
    return (
      <button onClick={mintNftHandler} className='cta-button mint-nft-button'>
        Mint NFT
      </button>
    )
  }

  useEffect(() => {
    checkWalletIsConnected();
  }, [])

  return (
    <div className='main-app'>
      <h1>Scrappy Squirrels Tutorial</h1>
      <div>
        {connectWalletButton()}
      </div>
    </div>
  )
}



export default App;
